import React from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Message,
  Segment
} from "semantic-ui-react";
import { connect } from "react-redux";
import { NavLink, Redirect,Link } from "react-router-dom";
import { authLogin } from "../store/actions/auth";
import "./Login.css"
import { GoogleLogin } from 'react-google-login';
import axios from "axios";
import * as actionTypes from "./actionTypes";
import { authSuccess, authFail, checkAuthTimeout} from '../store/actions/auth'
import { endpoint, endpointauth } from "../../constants";
class LoginForm extends React.Component {
  state = {
    email: "",
    password: ""
  };



  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { email, password } = this.state;
    this.props.login(email, password);
  };

   responseGoogle = (response) => {
     console.log(response.tokenObj.id_token);

     axios
       .post(`${endpointauth}/auth/login/`)
       .then(res => {
         const token = res.data.tokens.access;
         console.log((res));
         console.log((token));
         const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
         localStorage.setItem("token", token);
         localStorage.setItem("expirationDate", expirationDate);
         dispatch(authSuccess(token));
         dispatch(checkAuthTimeout(3600));
       })
       .catch(err => {
         dispatch(authFail(err));
       });








  }
  render() {
    const { error, loading, token } = this.props;
    const { email, password } = this.state;
    if (token) {
      return <Redirect to="/" />;
    }
    return (
      <>

      <Grid
        textAlign="center"
        style={{ height: "100vh" }}
        verticalAlign="middle"
      >
        <Grid.Column style={{ maxWidth: 450 }}>

          {error && <p>{this.props.error.message}</p>}

          <React.Fragment >
            <Form size="large" onSubmit={this.handleSubmit}>



                <Segment stacked style={{borderRadius: "18px"}}>

                  <br></br>
                  <h4 style={{ textAlign: "center" }}>Log-in to your account</h4>
                  <br></br>




                <Form.Input
                  onChange={this.handleChange}
                  value={email}
                  name="email"
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="email"
                />
                <Form.Input
                  onChange={this.handleChange}
                  fluid
                  value={password}
                  name="password"
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                />

                <Button
                  color="teal"
                  fluid
                  size="large"
                  loading={loading}
                  disabled={loading}
                >
                  Login
                </Button>
                  <br></br>
                  <br></br>
                  <br></br>
                  <br></br>
                  <br></br>






                  <GoogleLogin
                    clientId="356944659457-dmhr16e2csrkouh8r3bmgisaggelk51j.apps.googleusercontent.com"
                    buttonText="Login"
                    onSuccess={this.responseGoogle}
                    onFailure={this.responseGoogle}
                    cookiePolicy={'single_host_origin'}
                  />




              </Segment>
            </Form>
            <Message>
              New to us? <NavLink to="/signup">Sign Up</NavLink>
            </Message>
          </React.Fragment>
        </Grid.Column>
      </Grid>





      </>
    );
  }
}

const mapStateToProps = state => {
  return {
    loading: state.auth.loading,
    error: state.auth.error,
    token: state.auth.token
  };
};

const mapDispatchToProps = dispatch => {
  return {
    login: (email, password) => dispatch(authLogin(email, password))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(LoginForm);
