import React from "react";
import { connect } from "react-redux";
import { Redirect, NavLink } from "react-router-dom";
import {
    Button,
    Card,
    Dimmer,
    Divider,
    Form,
    Grid,
    Header,
    Image,
    Label,
    Loader,
    Menu,
    Message,
    Segment,
    Select,
    Table,
} from "semantic-ui-react";
import {
    userProfileUpdateURL

} from "../constants";
import { authAxios } from "../utils";
import axios from "axios"
import { endpoint } from "../constants";

// import Select from 'react-select'
class ShippingAddressUpdate extends React.Component {

    state = {
        data1: '',
        error: null,
        loading: false,

        formData: {
            shopping_fast_name: "",
            shopping_last_name: "",
            shopping_phone: "",
            shopping_zip_code: "",
            shopping_address: "",
            shopping_district: "",
            shopping_division: "",
            id:""


        },
        saving: false,
        success: false


    };



    handleChange = e => {
        const { formData } = this.state;
        const updatedFormdata = {
            ...formData,
            [e.target.name]: e.target.value
        };
        this.setState({
            formData: updatedFormdata
        });
    };




    componentDidMount() {
        this.setState({ loading: true });



        authAxios

            .get(`${endpoint}/shippingaddresslistview/`)



            .then(res => {
                this.setState({ formData: res.data.results[0], loading: false });



            })
            .catch(err => {
                this.setState({ error: err, loading: false });
            });
    }






    handleSubmit = e => {
        this.setState({ saving: true });
        const { saving } = this.state
        e.preventDefault();
        // if (saving === "true") {
        this.handleUpdateAddress();
        // } 
    };

    handleChange = e => {
        const { formData } = this.state;
        const updatedFormdata = {
            ...formData,
            [e.target.name]: e.target.value
        };
        this.setState({
            formData: updatedFormdata
        });
    };



    handleUpdateAddress = () => {
        const { userID, activeItem } = this.props;
        const { formData } = this.state;
        axios
            .put(`${endpoint}/shippingaddressupdateview/${formData.id}/`, {
                ...formData,
            })
            .then(res => {
                this.setState({
                    saving: false,
                    success: true,
                    formData: { default: false }
                });
            })
            .catch(err => {
                this.setState({ error: err });
            });
    };



    render() {
        const { error, formData, success, saving } = this.state;
        return (
            <div className='container my-5'>
                <div className='row ml-1 mr-1'>

                    {/* <div className='col-md-2'>
                        <div class="row">
                            <div class="col-12">
                                <div class="list-group" id="list-tab" role="tablist"> <br></br>
                                    <NavLink to={"/userprofileupdate"} class="list-group-item list-group-item-action" id="list-messages-list" data-bs-toggle="list" role="tab" aria-controls="messages"> User Profile Update</NavLink>
                                    <NavLink to={"/profile"} class="list-group-item list-group-item-action" id="list-profile-list" data-bs-toggle="list" role="tab" aria-controls="profile">Profile</NavLink>
                                    <NavLink to={"/order"} class="list-group-item list-group-item-action" id="list-messages-list" data-bs-toggle="list" role="tab" aria-controls="messages">Rassword Reset</NavLink>
                                    <NavLink to={"/order"} class="list-group-item list-group-item-action" id="list-messages-list" data-bs-toggle="list" role="tab" aria-controls="messages">Password Update</NavLink>
                                    <NavLink to={"/order"} class="list-group-item list-group-item-action" id="list-messages-list" data-bs-toggle="list" role="tab" aria-controls="messages">Cancelled Orders</NavLink>

                                </div>
                            </div>
                        </div>
                    </div> */}


                    <div className='col-md-3'></div>






                    <div className='col-md-6'>
                        <Form onSubmit={this.handleSubmit} success={success} error={error}>
                            <div className='row'>
                                <div className="col-md-6">
                                    <br></br>

                                    <label for="inputEmail4" class="form-label">Fast Name</label>
                                    <Form.Input
                                        required
                                        name="shopping_fast_name"
                                        placeholder="Fast Name"
                                        onChange={this.handleChange}
                                        value={formData.shopping_fast_name}
                                    />
                                </div>
                                <div className="col-md-6">
                                    <br></br>
                                    <label for="inputEmail4" class="form-label">Last Name</label>


                                    <Form.Input
                                        required
                                        name="shopping_last_name"
                                        placeholder="Last Name"
                                        onChange={this.handleChange}
                                        value={formData.shopping_last_name}
                                    />
                                </div>
                                <div className="col-md-12">
                                    <br></br>

                                    <label for="inputAddress" class="form-label">Address</label>
                                    <Form.Input
                                        required
                                        name="shopping_address"
                                        placeholder="Address "
                                        onChange={this.handleChange}
                                        value={formData.shopping_address}
                                    />
                                </div>
                                <div className="col-6">
                                    <br></br>

                                    <label for="inputAddress2" class="form-label">District</label>
                                    <Form.Input
                                        required
                                        name="shopping_district"
                                        placeholder="District "
                                        onChange={this.handleChange}
                                        value={formData.shopping_district}
                                    />
                                </div>
                                <div className="col-md-6">
                                    <br></br>

                                    <label for="inputCity" class="form-label">Division</label>
                                    <Form.Input
                                        required
                                        name="shopping_division"
                                        placeholder=" Division"
                                        onChange={this.handleChange}
                                        value={formData.shopping_division}
                                    />
                                </div>
                                <div className="col-md-6">
                                    <br></br>

                                    <label for="inputZip" class="form-label">Phone</label>
                                    <Form.Input
                                        required
                                        name="shopping_phone"
                                        placeholder="phone "
                                        onChange={this.handleChange}
                                        value={formData.shopping_phone}
                                    />
                                    <br></br>
                                </div>

                                <div className="col-md-6">
                                    <br></br>

                                    <label for="inputZip" class="form-label">Zip Code</label>
                                    <Form.Input
                                        required
                                        name="shopping_zip_code"
                                        placeholder="Zip Code "
                                        onChange={this.handleChange}
                                        value={formData.shopping_zip_code}
                                    />
                                    <br></br>
                                </div>





                                {/* <div className="col-md-6">
                            <select
                                value ={formData.status}
                                name="status"
                                onChange={this.handleChange}
                            >
                                    <option value='new'> news</option>
                                    <option value='onshiping'>onshiping </option>
                            </select>

                        </div> */}



                                <Form.Button disabled={saving} loading={saving} primary>
                                    Update
                        </Form.Button>


                            </div>
                        </Form>


                    </div>


                </div>
            </div>




        );
    }
}





export default ShippingAddressUpdate;
