import React from "react";
import { connect } from "react-redux";
import { Redirect, NavLink } from "react-router-dom";
import {
    Button,
    Card,
    Dimmer,
    Divider,
    Form,
    Grid,
    Header,
    Image,
    Label,
    Loader,
    Menu,
    Message,
    Segment,
    Select,
    Table,
} from "semantic-ui-react";
import {
    userProfileUpdateURL

} from "../constants";
import { authAxios } from "../utils";
import axios from "axios"
import { endpoint } from "../constants";

// import Select from 'react-select'
class PasswordReset extends React.Component {

    state = {
        data1: '',
        error: null,
        loading: false,

        formData: {
            email: "",


        },
        saving: false,
        success: false


    };






    componentDidMount() {
        this.handleFetchUserProfile();
    }





    handleSubmit = e => {
        this.setState({ saving: true });
        const { saving } = this.state
        e.preventDefault();
        // if (saving === "true") {
        this.handleUpdateAddress();
        // } 
    };

    handleChange = e => {
        const { formData } = this.state;
        const updatedFormdata = {
            ...formData,
            [e.target.name]: e.target.value
        };
        this.setState({
            formData: updatedFormdata
        });
        console.log(formData)
    };



    handleUpdateAddress = () => {
        const { userID, activeItem } = this.props;
        const { formData } = this.state;
        axios
            .put(userProfileUpdateURL(4), {
                ...formData,
            })
            .then(res => {
                this.setState({
                    saving: false,
                    success: true,
                    formData: { default: false }
                });
                console.log('res', res)
                // this.props.callback();
            })
            .catch(err => {
                this.setState({ error: err });
                // console.log(err)
            });
    };



    handleFetchUserProfile = () => {
        this.setState({ loading: true });
        axios
            // .get("http://127.0.0.1:8000/shop/userprofile")
            .post('http://127.0.0.1:8000/auth/request-reset-email/')

            .then(res => {

                this.setState({
                    formData: res.data.results[0]
                });
                console.log(res.data)
            })
            .catch(err => {
                this.setState({ error: err, loading: false });
            });
    };



    render() {
        const { error, formData, success, saving } = this.state;


        return (
            <div className='container my-5'>
                <div className='row ml-1 mr-1'>


                    <div className='col-md-3'></div>






                    <div className='col-md-6'>
                        <Form onSubmit={this.handleSubmit} success={success} error={error}>
                            <div className='row'>

                                <div className="col-md-12">
                                    <br></br>
                                    <label for="inputEmail4" class="form-label">Email</label>


                                    <Form.Input
                                        required
                                        name="email"
                                        placeholder="Email"
                                        onChange={this.handleChange}
                                        value={formData.email}
                                    />
                                    <br></br>
                                </div>



                                <Form.Button disabled={saving} loading={saving} primary>
                                    SubMit
                                </Form.Button>


                            </div>
                        </Form>


                    </div>


                </div>
            </div>




        );
    }
}




export default PasswordReset;
