import "./Product.css";
import "./productlist.css"
import { useStateValue } from "./StateProvider";
import "./DeskTopHomepageLayout.css"
import DeskTopHomepageLayoutProduct from "./DeskTopHomepageLayoutProduct"
import React, { Component } from "react";
import axios from "axios";
import { endpoint } from "../constants";
// import { NavLink } from "react-bootstrap";
import { NavLink } from 'react-router-dom'
import Resizer from 'react-image-file-resizer';
// import RNFS from 'react-native-fs';


class DeskTopHomepageLayoutProductList__1 extends Component {





    state = {
        loading: false,
        error: null,
        data: [],
        newImage :""
    };

    componentDidMount() {
        this.setState({ loading: true });



        axios
            .get(`${endpoint}/dhplinp-1/`)

            .then(res => {
                this.setState({ data: res.data.results, loading: false });




            })
            .catch(err => {
                this.setState({ error: err, loading: false });
            });
    }

    fileChangedHandler(event) {
        console.log(event,'..........event.........')
        let reader = new FileReader();
        // var data = reader.readAsDataURL(event)
        // console.log(data)
        // const imageDate = '<some base64 data>';
        // RNFS.writeFile(event, this.imageData, 'base64')
        //     .then(() => console.log('Image converted to jpg and saved at ' + event));
        const xhr = new XMLHttpRequest();
        xhr.onload = () => {
            const reader = new FileReader();
            reader.onloadend = () => {
                callback(reader.result);
            };
            reader.readAsDataURL(xhr.response);
        };
        xhr.open('GET', url);
        xhr.responseType = 'blob';
        xhr.send();








        var fileInput = false
        if (event) {
            fileInput = true
        }
        if (fileInput) {
            try {
                Resizer.imageFileResizer(
                    reader.readAsDataURL(event),
                    300,
                    300,
                    'JPEG',
                    100,
                    0,

                    'base64',
                    200,
                    200,
                );
            } catch (err) {
                console.log(err)
            }
        }
    }

render (){
    const { data, newImage} = this.state
    console.log('nemimage',newImage)


    return (

        <div className='product'>
            <div className="product__info">
                <h4> Kenwood kMix Stand </h4>

            </div>
            <div className='row ' >



                    {data &&
                        data.map(v => {
                            var image =v.image 
                            return (<>

                                <div className='col-md-6'>
                                <NavLink to ={v.link}>
                                <DeskTopHomepageLayoutProduct
                                    id={v.id}
                                    title={v.name}
                                    image={v.image}

                                />
                                        {this.fileChangedHandler(image )}
                                </NavLink>
                                </div>
                               </>
                            );

                        })}


            </div>
            <h6> Kenwood kMix Stand </h6>
        </div>
    );
}
}
export default DeskTopHomepageLayoutProductList__1;
