import React, { Component, onMouseOver } from 'react'
import { NavLink, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import axios from "axios";
import {
    Button,
    Card,
    Dimmer,
    Divider,
    Form,
    Grid,
    Header,
    Image,
    Label,
    Loader,
    Menu,
    Message,
    Segment,
    Select,
    Table,
    Item
} from "semantic-ui-react";
import { productDetailURL, addToCartURL, OrderDetailURL } from "../constants";
import { fetchCart } from "../store/actions/cart";
import { authAxios } from "../utils";
import "./ProductDetail.css"
import $ from "jquery";
import CKEditor from "react-ckeditor-component";

import { endpointlocahost, endpoint } from "../constants";

class OrderCanclledStatusChange extends React.Component {
    state = {
        loading: false,
        error: null,
        data: '',

        formData: {
            user_cancelled_status: "",
            user_returns_status: "",
            cancelled_reason_sms :'',
        },
    };


    componentDidMount() {
        this.handleFetchItem();

    }
    handleChange = e => {
        const { formData } = this.state;
        const updatedFormdata = {
            ...formData,
            [e.target.name]: e.target.value
        };

        this.setState({
            formData: updatedFormdata
        });

        this.handleFetchChangeApply()
    };

    handleFetchItem = () => {
        const {
            match: { params }
        } = this.props;
        console.log(this.props)
        this.setState({ loading: true });
        axios
            // .get(OrderDetailURL(params.ordercancellerandreturnsID))
            .get(`${endpoint}/ordercancelledandreturnsdetail/${params.ordercanclledstatuschangeID}/`)

            .then(res => {
                console.log('data1', res.data)
                this.setState({ formData: res.data, loading: false });
            })
            .catch(err => {
                this.setState({ error: err, loading: false });
                console.log(err)
            });
    };


    handleFetchChangeApply = () => {
        const { data, formData } = this.state;

        const {
            match: { params }
        } = this.props;


        axios
            .put(`${endpoint}/ordercancelledandreturns/${params.ordercanclledstatuschangeID}/`, { ...formData})
            .then(res => {
                this.setState({ data: res.data, loading: false });
            })
            .catch(err => {
                this.setState({ error: err, loading: false });
            });

    };



    render() {
        const { data, formData } = this.state;
        var item = formData;


        return (
            <>

                <div className='container-fluid'>
                    <div className='row ml-1 mr-1'>
                        <div className='col-md-3'>
                            <br></br>
                            <div class="row">
                                <div class="col-12">
                                    <div class="list-group" id="list-tab" role="tablist"> <br></br>
                                        <NavLink to={"/profile"} class="list-group-item list-group-item-action" id="list-profile-list" data-bs-toggle="list" role="tab" aria-controls="profile">Profile</NavLink>
                                        <NavLink to={"#"} class="list-group-item list-group-item-action" id="list-messages-list" data-bs-toggle="list" role="tab" aria-controls="messages">Messages</NavLink>
                                        <NavLink to={"/order"} class="list-group-item list-group-item-action" id="list-messages-list" data-bs-toggle="list" role="tab" aria-controls="messages">Order</NavLink>
                                        {/* <NavLink to={"#"} class="list-group-item list-group-item-action" id="list-settings-list" data-bs-toggle="list" role="tab" aria-controls="settings">My Favorits</NavLink> */}
                                        {/* <NavLink to={"#"} class="list-group-item list-group-item-action" id="list-settings-list" data-bs-toggle="list" role="tab" aria-controls="settings">My Comments</NavLink> */}
                                        <NavLink to={"/logout"} class="list-group-item list-group-item-action" id="list-settings-list" data-bs-toggle="list" role="tab" aria-controls="settings">Settings</NavLink>
                                        <NavLink to={"/logout"} class="list-group-item list-group-item-action" id="list-settings-list" data-bs-toggle="list" role="tab" aria-controls="settings">LogOut</NavLink>
                                    </div>
                                </div>
                            </div>

                        </div>

                        <div className='col-md-9'>

                            <h4 >OrderDetail</h4>
                            <Table celled>
                                <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell>Image</Table.HeaderCell>
                                        <Table.HeaderCell>Status</Table.HeaderCell>
                                        <Table.HeaderCell>Cancelled</Table.HeaderCell>
                                        <Table.HeaderCell>Order ID</Table.HeaderCell>
                                    </Table.Row>
                                </Table.Header>
                                <Table.Body>


                                    <Table.Row key={item.id}>
                                        <Table.Cell style={{ textAlign: "center" }} ><img src={`${endpointlocahost}${item.imageUrl}/`} style={{ width: "45px", height: "45px" }} class="img-fluid" alt="..." /></Table.Cell>
                                        <Table.Cell style={{ textAlign: "center" }}>{item.status}</Table.Cell>

                                        <Table.Cell style={{ textAlign: "center" }}>

                                            <select
                                                class="form-select form-select-lg mb-3" aria-label=".form-select-lg example"                                                value={item.user_cancelled_status}
                                                name="user_cancelled_status"
                                                onChange={this.handleChange}
                                                value={item.user_cancelled_status}
                                            > Choose your option
                                                <option value='Defective' > Defective</option>
                                                <option value='Changeofmind' > Change of mind</option>
                                                <option value='Wrongsize/color/etc'>Wrong size/color/etc </option>
                                                <option value='' hidden> Open this select men</option>

                                            </select>







                                        </Table.Cell>

                                        <Table.Cell style={{ textAlign: "center" }}>{item.order_code}</Table.Cell>
                                    </Table.Row>

                                </Table.Body>
                            </Table>
                            <div class="mb-3">
                                <label for="exampleFormControlTextarea1" class="form-label" required>Example textarea</label>
                                <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"
                                    name="cancelled_reason_sms"
                                    onChange={this.handleChange}


                                ></textarea>
                            </div>






                            <button onClick={this.handleFetchChangeApply} type="button" class="btn btn-outline-secondary">Submit</button>





                        </div>
                    </div>
                </div>
            </>

        );
    }



}



const mapDispatchToProps = dispatch => {
    return {
        refreshCart: () => dispatch(fetchCart())
    };
};

export default withRouter(
    connect(
        null,
        mapDispatchToProps
    )(OrderCanclledStatusChange)
);
