import "./Product.css";
import "./productlist.css"
import { useStateValue } from "./StateProvider";
import "./DeskTopHomepageLayout.css"
import DeskTopHomepageLayoutProduct from "./DeskTopHomepageLayoutProduct"
import React, { Component } from "react";
import axios from "axios";
import Product from "./Product";
import { endpoint } from "../constants";
import { NavLink } from 'react-router-dom'

class LargestBannerLeftSiteB extends Component {





    state = {
        loading: false,
        error: null,
        data: []
    };
    componentDidMount() {
        this.setState({ loading: true });



        axios

            .get(`${endpoint}/LargestBannerLeftSiteBListView/`)



            .then(res => {
                this.setState({ data: res.data.results, loading: false });




            })
            .catch(err => {
                this.setState({ error: err, loading: false });
            });
    }



    render() {
        const { data } = this.state


        return (
            <>
                {data &&
                    data.map(v => {
                        return (

                            <div className='col-md-3' style={{ backgroundColor: '#FFFFFF' }}>
                                <NavLink to={v.link}>
                                    <img style={{ marginTop: '9px' }} src={v.image} class="img-thumbnail" alt="..." />
                                </NavLink>
                            </div>

                        );

                    })}



            </>
        );
    }
}
export default LargestBannerLeftSiteB;
