import React from "react";
import { connect } from "react-redux";
import { Redirect,NavLink } from "react-router-dom";
// import Order from "./Order";

import {
  Button,
  Card,
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Label,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
  Table
} from "semantic-ui-react";

import {
  countryListURL,
  addressListURL,
  addressCreateURL,
  addressUpdateURL,
  addressDeleteURL,
  userIDURL,
  endpoint

} from "../constants";
import { authAxios } from "../utils";
import axios from "axios";
import ReactPlayer from 'react-player'
import DropDown from "react-drop-down-searcher";




class Payments extends React.Component {

  state = {
    loading: false,
    error: null,
    data: []
  };



componentDidMount() {
    this.setState({ loading: true });

      window.scrollTo(0, 0);



    axios
      .get(`${endpoint}/AccountsView/`)

      .then(res => {
        this.setState({ data: res.data.results, loading: false });
        window.scrollTo(0, 0);


      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  }


   Dropdown = React.createClass({
    getInitialState: function() {
      return {
        listVisible: false
      };
    },

    select: function(item) {
      this.props.selected = item;
    },

    show: function() {
      this.setState({ listVisible: true });
      document.addEventListener("click", this.hide);
    },

    hide: function() {
      this.setState({ listVisible: false });
      document.removeEventListener("click", this.hide);
    },
    renderListItems: function() {
        var items = [];
        for (var i = 0; i < this.props.list.length; i++) {
          var item = this.props.list[i];
          items.push(<div onClick={this.select.bind(null, item)}>
            <span style={{ color: item.hex }}>{item.name}</span>
          </div>);
        }
        return items;
      }
    });

render() {
    const { data, error, loading } = this.state;


    window.scrollTo(0, 0);

    return (


        <div className='container '  style ={{backgroundColor:"#FAFAFA"}}>
            <div className='row '  style ={{backgroundColor:"#FAFAFA"}}>


            <div className='col-md-12'>


                    <div className='row' style={{marginTop:'100px',}}>


                          <div className='col-md-7' >
                          <div className='row' >
                          <div className='col-md-12' >

                            <br></br>


                                <ReactPlayer
                                controls	
                                width ='513px'
                                height = '288px'
                                url='https://www.youtube.com/watch?v=Sa1FTulMYK0' 
                                />
                            </div>

                          <div  className='col-md-12 my-5'   style={{ fontWeight:"bold", color:"#DE1570"}}>
                            <h2>FAQ</h2>
                          </div>


                          <div  className='col-md-12 '   style={{ fontWeight:"bold", color:"#DE1570"}}>



                            <select style={{border:'none',fontWeight:'bold', fontSize:"1px" ,background:'white',height:'40px'}}  class="form-select form-select-lg mb-3" aria-label=".form-select-lg example">
                            <option  selected>একাউন্ট কিভাবে খুলবেন</option>
                            <option value="1">One</option>
                            <option value="2">Two</option>
                            <option hidden value="3">Three  select menu ,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,, ,,,,,,,,,,,,,,,,,,</option>
                            </select>


                        </div>











                        </div>
                      </div>

                      <div className="col-md-5  " id="list-tab" style={{  borderRadius:'10px',marginBottom:'20px',backgroundColor:'#FAFAFA'}}>

                        <div className="row  list-group list-group-item list-group-item-action" style={{ padding:0,margin:'auto', backgroundColor:'#FAFAFA', padding:0, textAlign:'center' ,height:"400px"}} id="list-profile-list" >




                        <div className='col-md-12 ' >
                          <div className='row ' style = {{padding:'0px',textAlign:'center',margin:'auto'}}>



                              <div className='col-12 col-md-12' style={{padding:'0px',backgroundColor:'#FC03A1'}} >



