    handleFetchUserProfile = () => {
        this.setState({ loading: true });
        const { error, formData, success, saving } = this.state;

        axios
            // .get("http://127.0.0.1:8000/shop/userprofile")
            .post('http://127.0.0.1:8000/auth/request-reset-email/', { ...formData})

            .then(res => {

                this.setState({
                    formData: res.data.results[0]
                });
                console.log(res.data)
            })
            .catch(err => {
                this.setState({ error: err, loading: false });
            });
    };



    render() {
        const { error, formData, success, saving } = this.state;


        return (
            <div className='container my-5'>
                <div className='row ml-1 mr-1'>


                    <div className='col-md-3'></div>






                    <div className='col-md-6'>
                        <Form success={success} error={error}>
                            <div className='row'>

                                <div className="col-md-12">
                                    <br></br>
                                    <label for="inputEmail4" class="form-label">Email</label>


                                    <Form.Input
                                        required
                                        name="email"
                                        placeholder="Email"
                                        onChange={this.handleChange}
                                        value={formData.email}
                                    />
                                    <br></br>
                                </div>



                                <Form.Button disabled={saving} loading={saving} primary>
                                    SubMit
                                </Form.Button>


                            </div>
                        </Form>


                    </div>


                </div>
            </div>




        );
    }
}




export default PasswordResetEmail;
