import React, { Component } from "react";
import {
  CardElement,
  injectStripe,
  Elements,
  StripeProvider
} from "react-stripe-elements";
import {
  Button,
  Container,
  Divider,
  Form,
  Header,
  Item,
  Label,
  Message,
  Select
} from "semantic-ui-react";
import { Link, withRouter,NavLink } from "react-router-dom";
import { authAxios } from "../utils";
import {
  checkoutURL,
  orderSummaryURL,
  addCouponURL,
  addressListURL
} from "../constants";
import ShippingAddress from "./ShippingAddress"

class CheckoutForm extends Component {
  state = {
    data: null,
    loading: false,
    error: null,
    success: false,
    shippingAddresses: [],
    billingAddresses: [],
    selectedBillingAddress: "",
    selectedShippingAddress: ""
  };

  componentDidMount() {

  }







  submit = ev => {
    ev.preventDefault();

    this.setState({ loading: true });
          authAxios

            .post(checkoutURL, {

              selectedBillingAddress,
              selectedShippingAddress
            })
            .then(res => {
              this.setState({ loading: false, success: true });
              this.props.history.push('/');
            })
            .catch(err => {
              this.setState({ loading: false, error: err });
              console.log (err.response.data)
            });

      // });

  };

  render() {
    const {
      data,
      error,
      loading,
      success,
      billingAddresses,
      shippingAddresses,
      selectedBillingAddress,
      selectedShippingAddress
    } = this.state;

    return (
      <div className='container'>
        <div className='row'>
        <div className ='col-md-6'>
            <ShippingAddress/>
        </div>












                    <Button
                      loading={loading}
                      disabled={loading}
                      primary
                      onClick={this.submit}
                      style={{ marginTop: "10px" }}
                    >
                      Submits
                  </Button>
        </div>

      </div>
    );
  }
}

const InjectedForm = withRouter(injectStripe(CheckoutForm));

const WrappedForm = () => (
  <Container text>
    <StripeProvider apiKey='null'>
      <div>
        <h1>Complete your order</h1>
        <Elements>
          <InjectedForm />
        </Elements>
      </div>
    </StripeProvider>
  </Container>
);

export default WrappedForm;
