//Owl Carousel Libraries and Module
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import { Link, withRouter, NavLink } from "react-router-dom";
import Sidebar from "./Sidebar"
import './Header.css'
import React from "react";
import "./Header2.css"
import './Home.css'
import { endpoint } from "../constants";
import axios from "axios"
//Owl Carousel Settings
const options = {
    // margin: 30,
    autoWidth:true,
    navText: [],
    responsiveClass: true,
    nav: true,
    autoplay: false,
    smartSpeed: 1000,

    responsive: {
        0: {
            items: 1,
        },
        400: {
            items: 1,
        },
        600: {
            items: 2,
        },
        700: {
            items: 3,
        },
        1000: {
            items: 15,
        }
    },
};
class HeaderBottom extends React.Component {







    state = {
        data: []
    };

    componentDidMount() {
        this.handleFetchPayments();
    }

    handleFetchPayments = () => {
        this.setState({ loading: true });
        axios
            .get(`${endpoint}/allcategorylistview/`)

            .then(res => {
                this.setState({
                    loading: false,
                    data: res.data.results
                });
                console.log(res.data.results,',,,,,,,,,,,,,,,,,,,,,,')

            })
            .catch(err => {
                this.setState({ error: err, loading: false });

            });
    };

















    render() {
        const { data } = this.state

        return (

                <OwlCarousel {...options} >
                    <NavLink to="/products" style={{ textDecoration: 'none', color: 'white' }}>
                        <span className="secondHeader__btn sidebar" >
                            Products 
                        </span> 
                    </NavLink>  

                {data &&
                    data.map(v => {
                        return (
                            <NavLink to="/products" style={{ textDecoration: 'none', color: 'white' }}>

<span className="secondHeader__btn">  Today's Deals </span>
<span className="secondHeader__btn"> Computers </span>
<span className="secondHeader__btn"> Pantry </span>
 <span className="secondHeader__btn"> Books </span>
                            </NavLink>  
                       );

                    })}

                </OwlCarousel>

        )
    };
}
export default HeaderBottom;

















