import React, { Component, onMouseOver } from 'react'
import { withRouter,Link } from "react-router-dom";
import { connect } from "react-redux";
import axios from "axios";
import {
  Button,
  Card,
  Container,
  Dimmer,
  Form,
  Grid,
  Header,
  Icon,
  Image,
  Item,
  Label,
  Loader,
  Message,
  Segment,
  Select,
  Divider,
  Radio
} from "semantic-ui-react";
import { productDetailURL, addToCartURL } from "../constants";
import { fetchCart } from "../store/actions/cart";
import { authAxios } from "../utils";
import "./ProductDetail.css"
import $ from "jquery";
import './Header.css'
import CKEditor from "react-ckeditor-component";
import ProductDetailSliderTop from "./ProductDetailSliderTop"
import ProductDetailSliderMiddel from "./ProductDetailSliderMiddel"
import ProductDetailSliderFooter from "./ProductDetailSliderFooter"
import './zoomsl.js'
import './Home.css'
import { useWindowScroll } from 'react-use';
import ScrollToTop from './ScrollToTop'

class ProductDetail extends React.Component {
  state = {
    loading: false,
    error: null,
    formVisible: false,
    data: [],
    formData: {},
    radio_name: "",
    set_name: "",
    id: "",
    activateAddDisabledValue :null
  };


  componentDidMount() {
    this.handleFetchItem();
    this.imageCheck();
    this.setState({ loading: true });
    // this.ImageZoom()
    window.scrollTo(0,0);


  }

















ImageZoom =() =>{

  console.log($($(".big_img")))

    $(".big_img").imagezoomsl({
      zoomrange: [3, 3]
    })



}


  handleToggleForm = () => {
    const { formVisible } = this.state;
    this.setState({
      formVisible: !formVisible
    });
  };

  change_image = () => {
    // Image Zooms
    $('.image_id').on("mouseover", function () {
      console.log('click')
      $(".big_img").attr("src", $(this).attr("src"));


    });


    // Variation Items 
    $('.v__image').on("mouseover", function () {
      $(".big_img").attr("src", $(this).attr("src"));

      var color = ($(this).attr("color"))
      var size = ($(this).attr("size"))
      var quantity = ($(this).attr("size"))
      document.getElementById("color").innerHTML = ` Color : ${color}`;
      document.getElementById("size").innerHTML = ` Size : ${size}`;
      document.getElementById("quantity").innerHTML = ` Quantity : ${quantity}`;

      // document.getElementById("phone__color").innerHTML = ` Color : ${color}`;
      // document.getElementById("phone__size").innerHTML = ` Size : ${size}`;
      // document.getElementById("phone__quantity").innerHTML = ` Quantity : ${quantity}`;

    });

    $('.v__image').on("click", function () {
      var styleid = $(this).attr("id")

    });

  }

  imageCheck = () => {

    $(".small_img").on("hover", function () {
      $(".big_img").attr("src", $(this).attr("src"));

    });

  };


  handleFetchItem = () => {
    const {
      match: { params }
    } = this.props;
    // console.log(this.props)
    this.setState({ loading: true });
    axios
      .get(productDetailURL(params.productID))
      .then(res => {
        // console.log(res.data)
        this.setState({ data: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleFormatData = formData => {
    // convert {colour: 1, size: 2} to [1,2] - they're all variations
    return Object.keys(formData).map(key => {
      return formData[key];
    });
  };

  handleAddToCart = slug => {
    this.setState({ loading: true });
    const { formData } = this.state;
    const variations = this.handleFormatData(formData);
    authAxios
      .post(addToCartURL, { slug, variations })
      .then(res => {
        // console.log(res);
        this.props.refreshCart();
        this.setState({ loading: false });

      })
      .catch(err => {
        this.setState({ error: err, loading: false });
        // console.log(err.response.data);
      });
  };


  handleChange = (e, { name, value }) => {
    const { formData } = this.state;
    const { radio_name } = this.state;
    const updatedFormData = {
      ...formData,
      [name]: value
    };

    this.setState({ formData: updatedFormData });
    this.setState({ radio_name: value })


  };






  change_image1 = event => {
    const { name, title, id, className } = event.target;
    const { formData } = this.state;
    const { radio_name, set_name } = this.state;
    delete formData[set_name]
    const updatedFormData = {
      ...formData,
      [name]: Number(id)
    };
    this.setState({ formData: updatedFormData });
    this.setState({ radio_name: id })
    this.setState({ set_name: name })

    var removeId = formData[set_name]
    if (radio_name.length === 0) {
      var d = document.getElementById(id).style.border = 'black solid 2px'
    } else {
      document.getElementById(radio_name).style.border = 'none'
    }
    document.getElementById(id).style.border = 'black solid 2px'


    const { formVisible, activateAddDisabledValue } = this.state;
    this.setState({
      formVisible: !formVisible
    });
    this.setState({
      activateAddDisabledValue:"activate"
    });
    console.log('click')
  }

// ProductDetail 

  CreateProductDetail = () => {
    const { data } = this.state;

    return { __html: data.detail };
  }


  render() {
    const { data, error, formData, formVisible, loading, set_name } = this.state;
    const item = data;
    // for (let i of data) {
    //   console.log(i)
    // }
    // console.log(typeof (data.de))

    // console.log(data)

    return (
      <>
   <div className='home'>
        <div className="container-fluid"  style={{ backgroundColor:'#F2F3F7'}}>
          <div className='row' >






            {/* <div className='col-md-12'>
                {loading && (
                  <Segment>
                    <Dimmer active inverted>
                      <Loader inverted>Loading</Loader>
                    </Dimmer>

                    <Image src="/images/wireframe/short-paragraph.png" />
                  </Segment>
                )}
            </div> */}




          {/* <div> */}
            {/* .............................. Product Detail in Desktop............................. */}

                <divf className='col-md-12 phone__product__detail'>
                  <h5 className='title' id='title'>{item.title}</h5>

                </divf>
                  <div className="col-md-0.2 desktop__product__detail "  style={{marginTop:'12px',marginLeft:'5px'}}>
                  {data.variations &&
                    data.variations.map(v => {
                      return (
                        <li className='image__position'>
                          <button className="image__hover__effect__small__img">
                            <img id="image_id" onMouseEnter={this.change_image} src={`http://127.0.0.1:8000${v.image}`} className="small_img image_id" alt="" />
                          </button>
                        </li>
                      );
                    })}
                </div>

                <div className="col-md-5 ">
                  <p>{this.ImageZoom()}</p>
                  <img id='imagezoom' onMouseEnter={this.ImageZoom()} src={item.image} className="big_img img-fluid" alt="" />
                </div>

                <div className="col-md-6 " >
                  <h4 className='title desktop__product__detail' id='title'>{item.title}</h4>
                  <br></br>
                  <h5>{item.price} tk </h5>
                  <h5 className='color' id="color"> </h5>
                  <h5 className='size' id="size">  </h5>
                  <h5 className='quantity' id="quantity"> </h5>
                  <h5 style={this.st}> Shipping Time 5-6 days</h5>
                  <h6>{item.description}</h6>

                  <div className='row'>
                    <div className='col-md-7'>
                      {data.variations &&
                        data.variations.map(v => {
                          const name = v.title.toLowerCase();
                          const value = v.id
                          return (
                            <>

                              <Radio
                                style={{ display: "inline-block", display: "none" }}
                                className='radio__button'

                                name={v.title}
                                value={v.id}
                                checked={this.state.radio_name === v.id}

                                onChange={this.handleChange}

                              />

                              <button className='variant__image'  >
                                <Image
                                  name={name}
                                  id={value}

                                  src={`http://127.0.0.1:8000${v.image}`}
                                  title={v.id}
                                  color={v.color}
                                  size={v.size}
                                  className='v__image'

                                  style={{ display: "inline-block", width: "70px", height: "70px" }}
                                  onMouseEnter={this.change_image}
                                  onClick={this.change_image1}


                                  // onClick={this.handleChange}

                                  checked={this.state.radio_name === v.id}
                                  type="radio"


                                />
                              </button>


                            </>
                          );
                        })}

                    </div>
                  </div>
                  {/* {formVisible && ( */}
                  <React.Fragment>
                    <Divider />
                    <Form onSubmit={() => this.handleAddToCart(item.slug)}>

                      <Form.Button primary

                      // disabled={!this.state.activateAddDisabledValue}

                      >Add</Form.Button>
                    </Form>
                  </React.Fragment>
                  {/* )} */}
                </div>


            {/* .............................. Product Detail in Desktop End............................. */}




            {/* Inspired by your recent shopping trends */}
          {/* <dib className='row my-5 m-5'> */}

            <div className='col-md-12' style={{ backgroundColor: '#F2F3F7' }}>
              <Divider />
              <h1>Inspired by your recent shopping trends</h1>

              <ProductDetailSliderTop></ProductDetailSliderTop>

            </div>


              <ScrollToTop></ScrollToTop>


          {/* Products related to this item */}

            <div className='col-md-12' >

              <hr></hr>
              <h1>Products related to this item</h1>
              <ProductDetailSliderMiddel></ProductDetailSliderMiddel>
            </div>



          <hr></hr>


          {/* ProductDetail */}

            <div className='col-md-8 desktop__product__detail_description ' style={{ backgroundColor:'#FFFFFF'}} >
                <div dangerouslySetInnerHTML={this.CreateProductDetail()} ></div>
            </div>


            <div className='col-md-3 desktop__product__detail_description__sidebar ' style={{ backgroundColor: '#FFFFFF' }} >

            </div>



          <div className = 'col-md-12'>
              <ProductDetailSliderFooter></ProductDetailSliderFooter>
          </div>




      {/*  */}


        </div>
        </div>
        </div>
      </>

    );
  }
}



const mapDispatchToProps = dispatch => {
  return {
    refreshCart: () => dispatch(fetchCart())
  };
};

export default withRouter(
  connect(
    null,
    mapDispatchToProps
  )(ProductDetail)
);
