import React from "react";
import {
  Container,
  Dimmer,
  Header,
  Icon,
  Image,
  Label,
  Loader,
  Table,
  Button,
  Message,
  Segment,
  Item
} from "semantic-ui-react";
import { connect } from "react-redux";
import { Link, Redirect, NavLink } from "react-router-dom";
import { authAxios } from "../utils";
import {
  addToCartURL,
  orderSummaryURL,
  orderItemDeleteURL,
  orderItemUpdateQuantityURL
} from "../constants";
import { endpoint, endpointlocahost } from "../constants";
import { fetchCart } from "../store/actions/cart";

class OrderSummary extends React.Component {
  state = {
    error: null,
    loading: false
  };

  componentDidMount() {
    // this.handleFetchOrder();
    window.scrollTo(0, 0);

  }
  // const { authenticated, cart, loading } = this.props;



  // handleFetchOrder = () => {
  //   this.setState({ loading: true });
  //   authAxios
  //     .get(orderSummaryURL)
  //     .then(res => {
  //       this.setState({ data: res.data, loading: false });
  //       window.scrollTo(0, 0);


  //     })
  //     .catch(err => {

  //       // if (err.response.status === 404) {
  //       //   this.setState({
  //       //     error: "You currently do not have an order",
  //       //     loading: false
  //       //   });
  //       // } else {
  //       //   this.setState({ error: err, loading: false });
  //       // }
  //     });
  // };

  renderVariations = orderItem => {
    let text = "";
    orderItem.item_variations.forEach(iv => {
      text += `${iv.variation}: ${iv.value}, `;
    });
    return text;
  };

  handleFormatData = itemVariations => {
    // convert [{id: 1},{id: 2}] to [1,2] - they're all variations
    return Object.keys(itemVariations).map(key => {
      return itemVariations[key].id;
    });
  };

  handleAddToCart = (slug, itemVariations) => {
    this.setState({ loading: true });
    const variations = this.handleFormatData(itemVariations);
    authAxios
      .post(addToCartURL, { slug, variations })
      .then(res => {
        fetchCart()
        this.setState({ loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleRemoveQuantityFromCart = slug => {
    authAxios
      .post(orderItemUpdateQuantityURL, { slug })
      .then(res => {
        fetchCart()
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleRemoveItem = itemID => {
    authAxios
      .delete(orderItemDeleteURL(itemID))
      .then(res => {
        fetchCart()
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  render() {
    const { error } = this.state;
    const { authenticated, cart, loading } = this.props;
    var data = cart

    const { isAuthenticated } = this.props;
    if (!isAuthenticated) {
      return <Redirect to="/login" />;
    }


    return (
  <>

    <Container >
      <div className='order__hidden__desktop'  style = {{textAlign:"center"}}>
        <h5>Order Summary</h5>
        {error && (
          <Message
            error
            header="There was an error"
            content={JSON.stringify(error)}
          />
        )}

        {data && (
          <Table celled>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Image</Table.HeaderCell>
                <Table.HeaderCell> Name</Table.HeaderCell>
                <Table.HeaderCell> Price</Table.HeaderCell>
                <Table.HeaderCell>Quantity</Table.HeaderCell>
                <Table.HeaderCell>Total item price</Table.HeaderCell>
              </Table.Row>
