import React from "react";
import { connect } from "react-redux";
import { Redirect,NavLink } from "react-router-dom";
// import Order from "./Order";

import {
  Button,
  Card,
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Label,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
  Table
} from "semantic-ui-react";

import {
  countryListURL,
  addressListURL,
  addressCreateURL,
  addressUpdateURL,
  addressDeleteURL,
  userIDURL,
  endpoint

} from "../constants";
import { authAxios } from "../utils";
import axios from "axios";
import ReactPlayer from 'react-player'




class Skills extends React.Component {

  state = {
    loading: false,
    error: null,
    data: []
  };



componentDidMount() {
    this.setState({ loading: true });

      window.scrollTo(0, 0);



    axios
      .get(`${endpoint}/AccountsView/`)

      .then(res => {
        this.setState({ data: res.data.results, loading: false });
        window.scrollTo(0, 0);


      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  }



render() {
    const { data, error, loading } = this.state;


    window.scrollTo(0, 0);

    return (


        <div className='container '  style ={{backgroundColor:"#FAFAFA"}}>
            <div className='row '  style ={{backgroundColor:"#FAFAFA"}}>












            <div className='col-md-12'>


                    <div className='row' style={{marginTop:'70px',}}>


                          <div className='col-md-6' >
                          <div className='row' >
                          <div className='col-md-12 my-5'   style={{display:'inline-block',padding:0}}>
                            <h2 style={{fontWeight:'bold'}}> ঘরে বসে Spoken English</h2>



                          </div>
                            <div className='col-md-2' style ={{padding:0}}>

                            <img style={{display:'inline-block',width:'70px',height:'70px',}} src="https://lms10.s3.ap-southeast-1.amazonaws.com/skill-up/spoken-english/instructor.jpg" class="ui medium circular image"/>
                          </div>
                          <div className='col-md-8'   style={{display:'inline-block',padding:0}}>

                          <h5 style={{display:'inline-block',color:'red'}}> Munzereen Shahid</h5>


                            <p style={{display:'inline-block'}} > MS (English), University of Oxford (UK); BA, MA (English), University of Dhaka; IELTS: 8.5</p>
                          </div>
                          <div className='col-md-12 my-5'   style={{display:'inline-block'}}>
                            <h2> এই কোর্স সম্পর্কে</h2>



                          </div>

                          <div className='col-md-12 my-5'   style={{display:'inline-block'}}>
                            <h5 style={{fontWeight:'bold'}}> ১. কোর্সটি কাদের জন্যে? </h5>
                            <p style={{marginLeft:'30px',fontSize:'16px'}}> * যারা স্পোকেন ইংলিশ শেখার কথা ভাবছেন 


                         </p>      <p style={{marginLeft:'30px',fontSize:'16px'}}>* যারা স্পোকেন ইংলিশ শেখার কথা ভাবছেন 


                         </p> 



                          </div>

















                        </div>
                      </div>

                      <div className="col-md-6 list-group" id="list-tab">
                        <div className="row list-group-item list-group-item-action " id="list-profile-list" >



                          <div className='col-md-12' style={{padding:0}}>

                            <br></br>
                              <div style={{borderRadius:"10px"}} class="list-group" id="list-tab" role="tablist">

                                <div class="list-group-item list-group-item-action" id="list-profile-list" data-bs-toggle="list"  role="tab" aria-controls="profile">

                                  <ReactPlayer
                                  controls	
                                  width ='513px'
                                  height = '288px'
                                  url='https://www.youtube.com/watch?v=Sa1FTulMYK0' 
                                  />
                                </div>
                              </div>
                          </div>
                        </div>
                      </div>






















                    </div>



                </div>
            </div>


        </div>


    );
}
}


const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

export default connect(mapStateToProps)(Skills);
