import React, { Component, onMouseOver } from 'react'
import { NavLink, withRouter, Link } from "react-router-dom";
import { connect } from "react-redux";
import axios from "axios";
import {
    Button,
    Card,
    Dimmer,
    Divider,
    Form,
    Grid,
    Header,
    Image,
    Label,
    Loader,
    Menu,
    Message,
    Segment,
    Select,
    Table,
    Item
} from "semantic-ui-react";
import { endpointauth } from "../constants";
import { fetchCart } from "../store/actions/cart";
import { authAxios } from "../utils";
import "./ProductDetail.css"

import $ from "jquery";
import CKEditor from "react-ckeditor-component";

import { endpointlocahost, endpoint } from "../constants";

class Searching extends React.Component {
    componentDidMount() {
        this.handleChange()

    }

    constructor(props) {
        super(props);
        this.state = {
            query: '',
            results: [],
            data: "",
            loading: false,
            message: '',
            totalResults: 0,
            totalPages: 0,
            currentPageNo: 0,
            active: "",
        };

        this.cancel = '';
    }

    fetchSearchResults = (updatedPageNo = '', query) => {
        const pageNumber = updatedPageNo ? `&page=${updatedPageNo}` : '';
        const searchUrl = `${endpoint}/search/?page=${pageNumber}&search=${query}`;
        if (this.cancel) {
            this.cancel.cancel();
        }
        localStorage.setItem("query", query)

        this.cancel = axios.CancelToken.source();

        axios.get(searchUrl, {
            cancelToken: this.cancel.token
        })
            .then(res => {
                this.setState({ results: res.data.results, loading: false });
            })
            .catch(error => {
                if (axios.isCancel(error) || error) {
                    this.setState({
                        loading: false,
                        message: 'Failed to fetch the data. Please check network'
                    })
                }
            })


    };

    handleChange = () => {

        const {
            match: { params }
        } = this.props;
        this.setState({ loading: true })
        const query = params.searchingData
        // let remText = query.replace(/\s/g, "")
        // let length = query.length;
        if (!query) {
            this.setState({ query, results: {}, message: '', totalPages: 0, totalResults: 0 });
        } else {
            this.setState({ query, loading: true, message: '' }, () => {
                this.fetchSearchResults(1, query);
            });
        }


    };


    OwnhandleChange = () => {

        const {
            match: { params }
        } = this.props;
        this.setState({ loading: true })
        const query = params.searchingData
        // let remText = query.replace(/\s/g, "")
        // let length = query.length;
        if (!query) {
            this.setState({ query, results: {}, message: '', totalPages: 0, totalResults: 0 });
        } else {
            this.setState({ query, loading: true, message: '' }, () => {
                this.fetchSearchResults(1, query);
            });
        }


    };







    render() {
        const { data, results,loading } = this.state;
        const {
            match: { params }
        } = this.props;
        const Thisquery = params.searchingData

        var query = localStorage.getItem("query")
        if (Thisquery == query) {
        }
        else {
            this.OwnhandleChange(Thisquery)
        }
        localStorage.setItem("query", Thisquery)





        return (
            <>

                <div class="container-fluid">
                    <div class="row home ml-1 mr-1">

                        <div className='col-md-12'>
                            <Grid.Column>

                                {loading && (
                                    <Segment>
                                        <Dimmer active inverted>
                                            <Loader inverted>Loading</Loader>
                                        </Dimmer>
                                        {/* <Image src="/images/wireframe/short-paragraph.png" /> */}
                                    </Segment>
                                )}
                            </Grid.Column>
                        </div>








