import axios from "axios";
import * as actionTypes from "./actionTypes";
import { endpoint, endpointauth } from "../../constants";

export const authStart = () => {
  return {
    type: actionTypes.AUTH_START
  };
};

export const authSuccess = token => {
  return {
    type: actionTypes.AUTH_SUCCESS,
    token: token
  };
};

export const authFail = error => {
  return {
    type: actionTypes.AUTH_FAIL,
    error: error
  };
  console.log(error)

};

export const logout = () => {
  localStorage.removeItem("token");
  localStorage.removeItem("expirationDate");
  return {
    type: actionTypes.AUTH_LOGOUT
  };
};

export const checkAuthTimeout = expirationTime => {
  return dispatch => {
    setTimeout(() => {
      dispatch(logout());
    }, expirationTime * 1000);
  };
};

export const authLogin = (email, password) => {
  return dispatch => {
    dispatch(authStart());
    axios
      .post(`${endpointauth}/auth/login/`, {
        email: email,
        password: password
      })
      .then(res => {
        const token = res.data.tokens.access;
        console.log((res));
        console.log((token));
        const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
        localStorage.setItem("token", token);
        localStorage.setItem("expirationDate", expirationDate);
        dispatch(authSuccess(token));
        dispatch(checkAuthTimeout(3600));
      })
      .catch(err => {
        dispatch(authFail(err));
      });
  };
};

export const authSignup = (username, email, password, password2) => {

  return dispatch => {
    dispatch(authStart());
    axios
      .post(`${endpointauth}/auth/register/`, {
        username: username,
        email: email,
        password: password,
        password2: password2
      })
      .then(res => {
        const token = res.data.key;
        const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
        localStorage.setItem("token", token);
        localStorage.setItem("expirationDate", expirationDate);
        dispatch(authSuccess(token));
        dispatch(checkAuthTimeout(3600));
      })
      .catch(err => {
        dispatch(authFail(err));
        console.log(err.response.data.errors)
        console.log(err.response.data.errors)
        var data = err.response.data
        console.log((Object.values(data).length))


        if (((Object.values(data).length)==1)) {
          console.log('is empty')
          console.log(err.response.data.errors.email)
          console.log(err.response.data.errors.username)

          var d = document.getElementsByClassName("error_messes").innerHTML
          console.log(d)

        } else {
          // document.getElementById(radio_name).style.border = 'none'

          console.log('is not empty ')


        }







      });
  };
};

export const authCheckState = () => {
  return dispatch => {
    const token = localStorage.getItem("token");
    if (token === undefined) {
      dispatch(logout());
    } else {
      const expirationDate = new Date(localStorage.getItem("expirationDate"));
      if (expirationDate <= new Date()) {
        dispatch(logout());
      } else {
        dispatch(authSuccess(token));
        dispatch(
          checkAuthTimeout(
            (expirationDate.getTime() - new Date().getTime()) / 1000
          )
        );
      }
    }
  };
};
