import React from "react";
import { connect } from "react-redux";
import { Redirect,NavLink } from "react-router-dom";
// import Order from "./Order";

import {
  Button,
  Card,
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Label,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
  Table
} from "semantic-ui-react";

import {
  countryListURL,
  addressListURL,
  addressCreateURL,
  addressUpdateURL,
  addressDeleteURL,
  userIDURL,
  endpoint,
  checkoutURL

} from "../constants";
import { authAxios } from "../utils";
import axios from "axios";
import ReactPlayer from 'react-player'




class Checkout extends React.Component {
    state = {
        data1: '',
        error: null,
        loading: false,

        formData: {
            transactionId: "",
            taka: "",


        },
        saving: false,
        success: false


    };


    handleSubmit = e => {
        this.setState({ saving: true });
        const { saving } = this.state
        e.preventDefault();
        // if (saving === "true") {
        this.handleUpdateAddress();
        // } 
    };

    handleChange = e => {
        const { formData } = this.state;
        const updatedFormdata = {
            ...formData,
            [e.target.name]: e.target.value
        };
        this.setState({
            formData: updatedFormdata
        });
    };



    submit = ev => {
        ev.preventDefault();
        const { error, formData, success, saving } = this.state;

        this.setState({ loading: true });


        authAxios

            .post(checkoutURL, {
           ...formData

            })
            .then(res => {
                this.setState({ loading: false, success: true });
                this.props.history.push('/');
                window.location.reload()
            })
            .catch(err => {

            });

        // });

    };






render() {
    const { error, formData, success, saving,loading } = this.state;
    console.log(formData)
    const isAuthenticated = localStorage.getItem('token')


    window.scrollTo(0, 0);

    return (


        <div className='container '  style ={{backgroundColor:"#FAFAFA"}}>
            <div className='row '  style ={{backgroundColor:"#FAFAFA"}}>


            <div className='col-md-12'>


                    <div className='row' style={{marginTop:'100px',}}>



                      <div className="col-md-5  " id="list-tab" style={{  borderRadius:'10px',marginBottom:'20px',backgroundColor:'#FAFAFA'}}>
                        <div className="row  list-group list-group-item list-group-item-action" style={{ padding:0,margin:'auto', backgroundColor:'#FAFAFA', padding:0, textAlign:'center' ,height:"400px"}} id="list-profile-list" >




                        <div className='col-md-12 ' >
                          <div className='row ' style = {{padding:'0px',textAlign:'center',margin:'auto'}}>



                              <div className='col-12 col-md-12' style={{padding:'0px',backgroundColor:'#FC03A1'}} >
                              <svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" fill="#FAFAFA" class="bi bi-emoji-smile" viewBox="0 0 16 16">
                                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
                                <path d="M4.285 9.567a.5.5 0 0 1 .683.183A3.498 3.498 0 0 0 8 11.5a3.498 3.498 0 0 0 3.032-1.75.5.5 0 1 1 .866.5A4.498 4.498 0 0 1 8 12.5a4.498 4.498 0 0 1-3.898-2.25.5.5 0 0 1 .183-.683zM7 6.5C7 7.328 6.552 8 6 8s-1-.672-1-1.5S5.448 5 6 5s1 .672 1 1.5zm4 0c0 .828-.448 1.5-1 1.5s-1-.672-1-1.5S9.448 5 10 5s1 .672 1 1.5z"/>
                            </svg>




                                <p style={{display:'inline-block',marginBottom:'15px',fontSize:'19px',marginTop:'10px'  , fontWeight:'bold', color:'#FAFAFA'}}>আপনার অর্ডার কনফার্ম করুন</p>
                                <p></p>



                              </div>



                              <Form onSubmit={this.handleSubmit} success={success} error={error}>
                            <div className='row'>
                                <div className="col-md-6">
                                    <br></br>

                                    <label for="inputEmail4" class="form-label"> Transaction ID</label>
                                    <Form.Input
                                        required
                                        name="transactionId"
                                        placeholder="Transaction ID"
                                        onChange={this.handleChange}
                                        value={formData.transactionId}
                                    />
                                </div>
                                <div className="col-md-6">
                                    <br></br>
                                    <label for="inputEmail4" class="form-label">The amount of money</label>


                                    <Form.Input
                                        required
                                        name="taka"
                                        placeholder="The amount of money"
                                        onChange={this.handleChange}
                                        value={formData.taka}
                                    />
                                </div>







                                <Button
                                    loading={loading}
                                    disabled={loading}
                                    primary
                                    onClick={this.submit}
                                    style={{ marginTop: "10px" ,backgroundColor:'#FC03A1' }}
                                >
                                    Confirm Order
                               </Button>


                            </div>
                        </Form>
















                          </div>
                        </div>














                        </div>
                      </div>






















                    </div>



                </div>
            </div>


        </div>


    );
}
}


const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

export default connect(mapStateToProps)(Checkout);
