import React from "react";
import { connect } from "react-redux";
import { Redirect, NavLink } from "react-router-dom";
import {
    Button,
    Card,
    Dimmer,
    Divider,
    Form,
    Grid,
    Header,
    Image,
    Label,
    Loader,
    Menu,
    Message,
    Segment,
    Select,
    Table,
} from "semantic-ui-react";
import {
    userProfileUpdateURL

} from "../constants";
import { authAxios } from "../utils";
import axios from "axios"
import { endpoint } from "../constants";

// import Select from 'react-select'
class UserProfileUpdate extends React.Component {

    state = {
        data1: '',
        error: null,
        loading: false,

        formData: {
            fastName: "",
            lastName: "",
            address: "",
            division: "",
            // image: "",
            district: "",
            phone: "",
            zip_code:'',

        },
        saving: false,
        success: false


    };






    componentDidMount() {
        this.handleFetchUserProfile();
    }





    handleSubmit = e => {
        this.setState({ saving: true });
        const { saving } = this.state
        e.preventDefault();
        // if (saving === "true") {
        this.handleUpdateAddress();
        // } 
    };

    handleChange = e => {
        const { formData } = this.state;
        const updatedFormdata = {
            ...formData,
            [e.target.name]: e.target.value
        };
        this.setState({
            formData: updatedFormdata
        });
        console.log(formData)
    };



    handleUpdateAddress = () => {
        const { userID, activeItem } = this.props;
        const { formData } = this.state;
        axios
            .put(userProfileUpdateURL(4), {
                ...formData,
            })
            .then(res => {
                this.setState({
                    saving: false,
                    success: true,
                    formData: { default: false }
                });
                console.log('res', res)
                // this.props.callback();
            })
            .catch(err => {
                this.setState({ error: err });
                // console.log(err)
            });
    };



    handleFetchUserProfile = () => {
        this.setState({ loading: true });
        authAxios
            // .get("http://127.0.0.1:8000/shop/userprofile")
            .get(`${endpoint}/userprofile/`)

            .then(res => {
                const { error, formData, success, saving, data1 } = this.state;

                this.setState({
                    formData: res.data.results[0]
                });
                console.log(res.data.results[0])
            })
            .catch(err => {
                this.setState({ error: err, loading: false });
            });
    };

    // options = [
    //     { value: 'chocolate', label: 'Chocolate' },
    //     { value: 'strawberry', label: 'Strawberry' },
    //     { value: 'vanilla', label: 'Vanilla' }
    // ]


    render() {
        const { error, formData, success, saving } = this.state;


        return (
            <div className='container my-5'>
                <div className='row ml-1 mr-1'>

                    {/* <div className='col-md-2'>
                        <div class="row">
