import React from "react";
import {
  Button,
  Form,
  Grid,
  Header,
  Message,
  Segment
} from "semantic-ui-react";
import { connect } from "react-redux";
import { NavLink, Redirect } from "react-router-dom";
import { authSignup } from "../store/actions/auth";

class RegistrationForm extends React.Component {
  state = {
    username: "",
    email: "",
    password: "",
    password2: "",
    data :""

  };



  handleSubmit = e => {
    e.preventDefault();
    const { username, email, password, password2 } = this.state;
    this.props.signup(username, email, password, password2);
  };

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { username, email, password, password2 ,data} = this.state;
    const { error, loading, token } = this.props;
    console.log(this.props)
    console.log(this.props)
    console.log('props', Object.values(this.props).length)
    if (Object.values(this.props).length > 1){

      // this.setState({ data: this.props.error});

      if ((this.props.error) ==null){
        // console.log(this.props.error.length)

      }
      else{
        console.log('is empty')
        var d = this.props.error.errors
        if (d == null){

        }
        else{
          console.log("ok")
          var err__email = this.props.error.errors.email
          console.log(String(email))


        }


      }




    }

    if (token) {
      return <Redirect to="/" />;
    }
    return (
      <Grid
        textAlign="center"
        style={{ height: "100vh" }}
        verticalAlign="middle"
      >
        <Grid.Column style={{ maxWidth: 450 }}>

          {/* {error && <p>{this.props.error.errors}</p>} */}
          <p className='error_messes'></p>

          <React.Fragment>
            <Form size="large" onSubmit={this.handleSubmit}>
              <Segment stacked style={{ borderRadius: "18px" }}>
                <br></br>
                <h4 style={{ textAlign: "center" }}>Signup to your account</h4>
                <br></br>








                <Form.Input
                  onChange={this.handleChange}
                  value={username}
                  name="username"
                  fluid
                  icon="user"
                  iconPosition="left"
                  placeholder="Username"
                />
                <Form.Input
                  onChange={this.handleChange}
                  value={email}
                  name="email"
                  fluid
                  icon="mail"
                  iconPosition="left"
                  placeholder="E-mail address"
                />
                <Form.Input
                  onChange={this.handleChange}
                  fluid
                  value={password}
                  name="password"
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                />
                <Form.Input
                  onChange={this.handleChange}
                  fluid
                  value={password2}
                  name="password2"
                  icon="lock"
                  iconPosition="left"
                  placeholder="Confirm password"
                  type="password"
                />

                <Button
                  color="teal"
                  fluid
                  size="large"
                  loading={loading}
                  disabled={loading}
                >
                  Signup
                </Button>
                <br></br>
                <br></br>
                <br></br>


              </Segment>
            </Form>
            <Message>
              Already have an account? <NavLink to="/login">Login</NavLink>
            </Message>
          </React.Fragment>
        </Grid.Column>
      </Grid>
    );
  }
}

const mapStateToProps = state => {
  return {
    loading: state.auth.loading,
    error: state.auth.error,
    token: state.auth.token
  };
};

const mapDispatchToProps = dispatch => {
  return {
    signup: (username, email, password, password2) =>
      dispatch(authSignup(username, email, password, password2))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(RegistrationForm);
