import React from "react";
import { connect } from "react-redux";
import { Redirect,NavLink } from "react-router-dom";
// import Order from "./Order";

import {
  Button,
  Card,
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Label,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
  Table
} from "semantic-ui-react";

import {
  countryListURL,
  addressListURL,
  addressCreateURL,
  addressUpdateURL,
  addressDeleteURL,
  userIDURL,
  endpoint

} from "../constants";
import { authAxios } from "../utils";
import axios from "axios";
import ReactPlayer from 'react-player'




class Payments extends React.Component {

  state = {
    loading: false,
    error: null,
    data: []
  };



componentDidMount() {
    this.setState({ loading: true });

      window.scrollTo(0, 0);



    axios
      .get(`${endpoint}/AccountsView/`)

      .then(res => {
        this.setState({ data: res.data.results, loading: false });
        window.scrollTo(0, 0);


      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  }



render() {
    const { data, error, loading } = this.state;


    window.scrollTo(0, 0);

    return (


        <div className='container '  style ={{backgroundColor:"#FAFAFA"}}>
            <div className='row '  style ={{backgroundColor:"#FAFAFA"}}>












            <div className='col-md-12'>


                    <div className='row' style={{marginTop:'70px',}}>


                          <div className='col-md-7' >
                          <div className='row' >
                          <div className='col-md-12' >

                            <br></br>


                                <ReactPlayer
                                controls	
                                width ='513px'
                                height = '288px'
                                url='https://www.youtube.com/watch?v=Sa1FTulMYK0' 
                                />
                            </div>

                          <div  className='col-md-12 my-5'   style={{ fontWeight:"bold", color:"#DE1570"}}>
                            <h2>FAQ</h2>



                          </div>











                        </div>
                      </div>

                      <div className="col-md-5  " id="list-tab" style={{  borderRadius:'10px',marginBottom:'20px',backgroundColor:'#FAFAFA'}}>
                        <div className="row  list-group list-group-item list-group-item-action" style={{ padding:0,margin:'auto', backgroundColor:'#FAFAFA', padding:0, textAlign:'center' ,height:"700px"}} id="list-profile-list" >




                        <div className='col-md-12 my-4' >
                          <div className='row ' style = {{textAlign:'center',margin:'auto'}}>



                              <div className='col-12 col-md-12' style={{padding:'0px',backgroundColor:'#FC03A1'}} >




                                <svg  style={{ display:'inline-block'}}  xmlns="http://www.w3.org/2000/svg" width="35" height="35" fill="#45C881" class="bi bi-alarm" viewBox="0 0 16 16">
                                  <path d="M8.5 5.5a.5.5 0 0 0-1 0v3.362l-1.429 2.38a.5.5 0 1 0 .858.515l1.5-2.5A.5.5 0 0 0 8.5 9V5.5z"/>
                                  <path d="M6.5 0a.5.5 0 0 0 0 1H7v1.07a7.001 7.001 0 0 0-3.273 12.474l-.602.602a.5.5 0 0 0 .707.708l.746-.746A6.97 6.97 0 0 0 8 16a6.97 6.97 0 0 0 3.422-.892l.746.746a.5.5 0 0 0 .707-.708l-.601-.602A7.001 7.001 0 0 0 9 2.07V1h.5a.5.5 0 0 0 0-1h-3zm1.038 3.018a6.093 6.093 0 0 1 .924 0 6 6 0 1 1-.924 0zM0 3.5c0 .753.333 1.429.86 1.887A8.035 8.035 0 0 1 4.387 1.86 2.5 2.5 0 0 0 0 3.5zM13.5 1c-.753 0-1.429.333-1.887.86a8.035 8.035 0 0 1 3.527 3.527A2.5 2.5 0 0 0 13.5 1z"/>
                                </svg>
                                <p style={{display:'inline-block',marginBottom:'15px',fontSize:'15px',marginTop:'10px'}}>আপনার অর্ডার</p>
                                <p></p>



                              </div>





                                <div className='col-6 ' >
                                <p style={{display:'inline-block',marginBottom:'15px',fontSize:'18px',color:'black', fontWeight:"bold",marginTop:'10px'}}>কোর্সের নাম</p>

                                </div>

                                <div className='col-6 ' >
                                    <p style={{display:'inline-block',marginBottom:'15px',fontSize:'18px', color:'black', fontWeight:"bold", marginTop:'10px'}}>মূল্য</p>
                                    <p></p>
                                </div>



                                <div className='col-6 ' >
                                <p style={{display:'inline-block',marginBottom:'15px',fontSize:'12px',color:'black',marginTop:'10px'}}>IELTS A 3 X Course for Beginers</p>

                                </div>

                                <div className='col-6 ' >
                                    <p style={{display:'inline-block',marginBottom:'15px',fontSize:'18px', color:'black', fontWeight:"bold", marginTop:'10px'}}>700 tk</p>
                                    <p></p>
                                </div>








                                <div className='col-6 ' >
                                <p style={{display:'inline-block',marginBottom:'15px',fontSize:'18px',color:'black', fontWeight:"bold",marginTop:'10px'}}>মোট </p>

                                </div>

                                <div className='col-6 ' >
                                    <p style={{display:'inline-block',marginBottom:'15px',fontSize:'18px', color:'black', fontWeight:"bold", marginTop:'10px'}}>400 tk</p>
                                    <p></p>
                                </div>











                          </div>
                        </div>

                      <div className = 'col-md-12'>
                        <div className = 'row'>



                        <div className='col-md-6 my-5' >
                                <h2 style = {{color: '#34315D',fontWeight:"bold"}}> <strike> ৳ 4,500</strike> ৳ 450</h2>
                              </div>


                              <div className='col-md-6 my-5' >
                              <button style = {{fontSize:'18px' ,backgroundColor:'#3BC57A', border:'none'}} type="button" class="btn btn-info">কোর্সটি  শুরু করুন  </button>


                              </div>




                        </div>
                      </div>













                        </div>
                      </div>






















                    </div>



                </div>
            </div>


        </div>


    );
}
}


const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

export default connect(mapStateToProps)(Payments);
